     1â†’/**
     2â†’ * PDF Generation Service
     3â†’ * Handles PDF generation, export, and preview using React-PDF
     4â†’ */
     5â†’
     6â†’import { writeFile } from "node:fs/promises";
     7â†’import { renderToBuffer } from "@react-pdf/renderer";
     8â†’import type { ThemeData } from "@shared/types/ipc-contracts.js";
     9â†’import { createPDFDocument } from "../pdf/Document.js";
    10â†’
    11â†’export interface CoverData {
    12â†’	hasCoverPage: boolean;
    13â†’	title?: string | null;
    14â†’	subtitle?: string | null;
    15â†’	author?: string | null;
    16â†’	logoPath?: string | null;
    17â†’	backgroundPath?: string | null;
    18â†’}
    19â†’
    20â†’/**
    21â†’ * Generate PDF buffer from markdown content
    22â†’ */
    23â†’export async function generatePDF(
    24â†’	content: string,
    25â†’	theme: ThemeData,
    26â†’	projectDir?: string,
    27â†’	coverData?: CoverData,
    28â†’	mermaidDiagrams?: Record<string, string>,
    29â†’): Promise<Buffer> {
    30â†’	try {
    31â†’		console.log("ğŸ“„ Generating PDF with theme:", theme.name);
    32â†’		console.log(
    33â†’			"ğŸ“ Content received (first 500 chars):",
    34â†’			content.substring(0, 500),
    35â†’		);
    36â†’
    37â†’		if (coverData?.hasCoverPage) {
    38â†’			console.log("ğŸ“‘ Cover page enabled:", coverData);
    39â†’		}
    40â†’
    41â†’		// Create PDF document element
    42â†’		const document = await createPDFDocument(
    43â†’			content,
    44â†’			theme,
    45â†’			projectDir,
    46â†’			coverData,
    47â†’			mermaidDiagrams,
    48â†’		);
    49â†’
    50â†’		// Render to buffer
    51â†’		const buffer = await renderToBuffer(document);
    52â†’
    53â†’		console.log("âœ… PDF generated successfully");
    54â†’		return Buffer.from(buffer);
    55â†’	} catch (error) {
    56â†’		console.error("âŒ PDF generation failed:", error);
    57â†’		throw new Error(
    58â†’			`Failed to generate PDF: ${error instanceof Error ? error.message : String(error)}`,
    59â†’		);
    60â†’	}
    61â†’}
    62â†’
    63â†’/**
    64â†’ * Export PDF to file system
    65â†’ */
    66â†’export async function exportPDF(
    67â†’	buffer: Buffer,
    68â†’	filePath: string,
    69â†’): Promise<void> {
    70â†’	try {
    71â†’		console.log("ğŸ’¾ Exporting PDF to:", filePath);
    72â†’
    73â†’		// Write buffer to file
    74â†’		await writeFile(filePath, buffer);
    75â†’
    76â†’		console.log("âœ… PDF exported successfully");
    77â†’	} catch (error) {
    78â†’		console.error("âŒ PDF export failed:", error);
    79â†’		throw new Error(
    80â†’			`Failed to export PDF: ${error instanceof Error ? error.message : String(error)}`,
    81â†’		);
    82â†’	}
    83â†’}
    84â†’
    85â†’/**
    86â†’ * Generate PDF for preview (returns base64 data URL)
    87â†’ */
    88â†’export async function previewPDF(
    89â†’	content: string,
    90â†’	theme: ThemeData,
    91â†’	projectDir?: string,
    92â†’	coverData?: CoverData,
    93â†’	mermaidDiagrams?: Record<string, string>,
    94â†’): Promise<string> {
    95â†’	try {
    96â†’		console.log("ğŸ‘ï¸  Generating PDF preview with theme:", theme.name);
    97â†’
    98â†’		// Generate PDF buffer
    99â†’		const buffer = await generatePDF(content, theme, projectDir, coverData, mermaidDiagrams);
   100â†’
   101â†’		// Convert to base64 data URL
   102â†’		const base64 = buffer.toString("base64");
   103â†’		const dataUrl = `data:application/pdf;base64,${base64}`;
   104â†’
   105â†’		console.log("âœ… PDF preview generated successfully");
   106â†’		return dataUrl;
   107â†’	} catch (error) {
   108â†’		console.error("âŒ PDF preview failed:", error);
   109â†’		throw new Error(
   110â†’			`Failed to generate PDF preview: ${error instanceof Error ? error.message : String(error)}`,
   111â†’		);
   112â†’	}
   113â†’}
   114â†’
   115â†’/**
   116â†’ * Calculate approximate page breaks based on theme and content
   117â†’ * This is a simplified estimation - actual page breaks depend on rendering
   118â†’ */
   119â†’export function calculatePageBreaks(
   120â†’	content: string,
   121â†’	theme: ThemeData,
   122â†’): number[] {
   123â†’	const pageBreaks: number[] = [];
   124â†’
   125â†’	// Look for explicit page break markers
   126â†’	const lines = content.split("\n");
   127â†’	let lineNumber = 0;
   128â†’
   129â†’	for (let i = 0; i < lines.length; i++) {
   130â†’		lineNumber++;
   131â†’
   132â†’		if (lines[i].trim() === "---PAGE_BREAK---") {
   133â†’			pageBreaks.push(lineNumber);
   134â†’		}
   135â†’	}
   136â†’
   137â†’	// Simple heuristic: estimate page capacity based on theme
   138â†’	const linesPerPage = Math.floor(
   139â†’		((theme.pageHeight - theme.marginTop - theme.marginBottom) * 72) /
   140â†’			(theme.bodySize * theme.leading),
   141â†’	);
   142â†’
   143â†’	console.log(
   144â†’		`ğŸ“ Estimated ${linesPerPage} lines per page with theme ${theme.name}`,
   145â†’	);
   146â†’
   147â†’	// Add automatic page breaks every N lines (if no explicit breaks)
   148â†’	if (pageBreaks.length === 0) {
   149â†’		const totalLines = lines.length;
   150â†’		let currentLine = linesPerPage;
   151â†’
   152â†’		while (currentLine < totalLines) {
   153â†’			pageBreaks.push(currentLine);
   154â†’			currentLine += linesPerPage;
   155â†’		}
   156â†’	}
   157â†’
   158â†’	return pageBreaks;
   159â†’}
