    46â†’/**
    47â†’ * Strip HTML tags and convert to markdown format
    48â†’ * Comprehensive conversion for all standard HTML elements
    49â†’ */
    50â†’function stripHTML(html: string): string {
    51â†’	let text = html;
    52â†’
    53â†’	// Remove script and style tags with their content
    54â†’	text = text.replace(
    55â†’		/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi,
    56â†’		"",
    57â†’	);
    58â†’	text = text.replace(/<style\b[^<]*(?:(?!<\/style>)<[^<]*)*<\/style>/gi, "");
    59â†’
    60â†’	// Convert headings to markdown format (h1-h6)
    61â†’	text = text.replace(/<h1[^>]*>(.*?)<\/h1>/gi, "\n\n# $1\n\n");
    62â†’	text = text.replace(/<h2[^>]*>(.*?)<\/h2>/gi, "\n\n## $1\n\n");
    63â†’	text = text.replace(/<h3[^>]*>(.*?)<\/h3>/gi, "\n\n### $1\n\n");
    64â†’	text = text.replace(/<h4[^>]*>(.*?)<\/h4>/gi, "\n\n#### $1\n\n");
    65â†’	text = text.replace(/<h5[^>]*>(.*?)<\/h5>/gi, "\n\n##### $1\n\n");
    66â†’	text = text.replace(/<h6[^>]*>(.*?)<\/h6>/gi, "\n\n###### $1\n\n");
    67â†’
    68â†’	// Convert code blocks (pre + code)
    69â†’	text = text.replace(
    70â†’		/<pre[^>]*><code[^>]*>(.*?)<\/code><\/pre>/gis,
    71â†’		"\n```\n$1\n```\n\n",
    72â†’	);
    73â†’	text = text.replace(/<pre[^>]*>(.*?)<\/pre>/gis, "\n```\n$1\n```\n\n");
    74â†’
    75â†’	// Convert blockquotes
