   115â†’/**
   116â†’ * Calculate approximate page breaks based on theme and content
   117â†’ * This is a simplified estimation - actual page breaks depend on rendering
   118â†’ */
   119â†’export function calculatePageBreaks(
   120â†’	content: string,
   121â†’	theme: ThemeData,
   122â†’): number[] {
   123â†’	const pageBreaks: number[] = [];
   124â†’
   125â†’	// Look for explicit page break markers
   126â†’	const lines = content.split("\n");
   127â†’	let lineNumber = 0;
   128â†’
   129â†’	for (let i = 0; i < lines.length; i++) {
   130â†’		lineNumber++;
   131â†’
   132â†’		if (lines[i].trim() === "---PAGE_BREAK---") {
   133â†’			pageBreaks.push(lineNumber);
   134â†’		}
   135â†’	}
   136â†’
   137â†’	// Simple heuristic: estimate page capacity based on theme
   138â†’	const linesPerPage = Math.floor(
   139â†’		((theme.pageHeight - theme.marginTop - theme.marginBottom) * 72) /
   140â†’			(theme.bodySize * theme.leading),
   141â†’	);
   142â†’
   143â†’	console.log(
   144â†’		`ğŸ“ Estimated ${linesPerPage} lines per page with theme ${theme.name}`,
   145â†’	);
   146â†’
   147â†’	// Add automatic page breaks every N lines (if no explicit breaks)
   148â†’	if (pageBreaks.length === 0) {
   149â†’		const totalLines = lines.length;
   150â†’		let currentLine = linesPerPage;
   151â†’
   152â†’		while (currentLine < totalLines) {
   153â†’			pageBreaks.push(currentLine);
   154â†’			currentLine += linesPerPage;
   155â†’		}
   156â†’	}
   157â†’
   158â†’	return pageBreaks;
   159â†’}
