    56â†’    const isLastLine = idx === lines.length - 1;
    57â†’
    58â†’    // Preserve leading whitespace for code indentation
    59â†’    const preservedLine = preserveLeadingWhitespace(line);
    60â†’
    61â†’    // Create TextRun for this line of code
    62â†’    // Empty lines still need a space or non-breaking space to maintain height
    63â†’    const textContent = preservedLine === '' ? '\u00A0' : preservedLine;
    64â†’
    65â†’    const textRun = new TextRun({
    66â†’      text: textContent,
    67â†’      font: 'Courier New',
    68â†’      size: pointsToHalfPoints(fontSize),
    69â†’      color: theme.textColor.replace('#', ''),
    70â†’    });
    71â†’
    72â†’    // Calculate spacing - add top margin for first line, bottom margin for last line
    73â†’    // Minimal spacing between lines for code blocks
    74â†’    const spacingBefore = isFirstLine ? pointsToDxa(12 * 0.75) : 0;
    75â†’    const spacingAfter = isLastLine ? pointsToDxa(12 * 0.75) : pointsToDxa(1);
    76â†’
    77â†’    paragraphs.push(
    78â†’      new Paragraph({
    79â†’        alignment: AlignmentType.LEFT,
    80â†’        shading: {
    81â†’          type: ShadingType.SOLID,
    82â†’          color: backgroundColor,
    83â†’          fill: backgroundColor,
    84â†’        },
    85â†’        // Add padding effect using indentation
    86â†’        indent: {
    87â†’          left: pointsToDxa(12), // 12pt padding left
    88â†’          right: pointsToDxa(12), // 12pt padding right
    89â†’        },
    90â†’        spacing: {
    91â†’          before: spacingBefore,
    92â†’          after: spacingAfter,
    93â†’          line: 360, // 1.5 line spacing (240 = single, 360 = 1.5)
    94â†’        },
    95â†’        children: [textRun],
    96â†’      })
    97â†’    );
    98â†’  }
