   138â†’ */
   139â†’function createListItemParagraph(
   140â†’  itemText: string,
   141â†’  theme: ThemeData,
   142â†’  ordered: boolean,
   143â†’  itemIndex: number,
   144â†’  indentLevel: number,
   145â†’  isLastItem: boolean
   146â†’): Paragraph {
   147â†’  const fontSize = theme.bodySize;
   148â†’
   149â†’  // Parse inline formatting for the list item
   150â†’  const inlineElements = parseInlineFormatting(itemText);
   151â†’
   152â†’  // Create prefix for the list item
   153â†’  const prefix = ordered ? `${itemIndex + 1}. ` : 'â€¢ ';
   154â†’
   155â†’  // Calculate left indent (20 points per indent level, converted to DXA)
   156â†’  // Plus base indent for the prefix
   157â†’  const baseIndent = pointsToDxa(15); // 15pt base indent for first level
   158â†’  const levelIndent = pointsToDxa(20); // 20pt additional per level
   159â†’  const leftIndent = baseIndent + (indentLevel * levelIndent);
   160â†’
   161â†’  // Hanging indent for the bullet/number (so text wraps properly)
   162â†’  const hangingIndent = pointsToDxa(15);
   163â†’
   164â†’  // Build children - prefix first, then inline content
   165â†’  const children: TextRun[] = [
   166â†’    new TextRun({
   167â†’      text: prefix,
   168â†’      font: theme.bodyFont,
   169â†’      size: pointsToHalfPoints(fontSize),
   170â†’      color: theme.textColor.replace('#', ''),
   171â†’    }),
   172â†’    ...convertInlineElements(inlineElements, theme, fontSize),
   173â†’  ];
   174â†’
   175â†’  // Use more spacing after the last item in the list
   176â†’  const spacingAfter = isLastItem
   177â†’    ? pointsToDxa(16 * 0.75) // 16px margin after list
   178â†’    : pointsToDxa(4 * 0.75); // 4px between items
   179â†’
   180â†’  return new Paragraph({
   181â†’    alignment: AlignmentType.LEFT,
   182â†’    indent: {
   183â†’      left: leftIndent,
   184â†’      hanging: hangingIndent,
   185â†’    },
   186â†’    spacing: {
   187â†’      after: spacingAfter,
   188â†’    },
   189â†’    children,
   190â†’  });
   191â†’}
   192â†’
   193â†’/**
   194â†’ * Convert a list markdown element to an array of docx Paragraphs
   195â†’ * Each list item becomes its own paragraph
   196â†’ */
   197â†’export function createListParagraphs(
   198â†’  element: MarkdownElement,
   199â†’  theme: ThemeData
   200â†’): Paragraph[] {
   201â†’  const paragraphs: Paragraph[] = [];
   202â†’  const items = element.items || [];
   203â†’  const indentLevels = element.indentLevels || [];
   204â†’  const ordered = element.ordered || false;
   205â†’
   206â†’  for (let idx = 0; idx < items.length; idx++) {
   207â†’    const item = items[idx];
   208â†’    const indentLevel = indentLevels[idx] || 0;
   209â†’    const isLastItem = idx === items.length - 1;
   210â†’
   211â†’    paragraphs.push(
   212â†’      createListItemParagraph(item, theme, ordered, idx, indentLevel, isLastItem)
   213â†’    );
   214â†’  }
   215â†’
   216â†’  return paragraphs;
   217â†’}
