    21â†’
    22â†’/**
    23â†’ * Convert points to half-points (used for font sizes in docx)
    24â†’ * Font sizes in docx are specified in half-points (1 point = 2 half-points)
    25â†’ */
    26â†’function pointsToHalfPoints(points: number): number {
    27â†’  return Math.round(points * 2);
    28â†’}
    29â†’
    30â†’/**
    31â†’ * Map heading levels to docx HeadingLevel enum
    32â†’ */
    33â†’function getHeadingLevel(level: number): HeadingLevel {
    34â†’  const headingLevels: Record<number, HeadingLevel> = {
    35â†’    1: HeadingLevel.HEADING_1,
    36â†’    2: HeadingLevel.HEADING_2,
    37â†’    3: HeadingLevel.HEADING_3,
    38â†’    4: HeadingLevel.HEADING_4,
    39â†’    5: HeadingLevel.HEADING_5,
    40â†’    6: HeadingLevel.HEADING_6,
    41â†’  };
    42â†’  return headingLevels[level] || HeadingLevel.HEADING_1;
    43â†’}
    44â†’
    45â†’/**
    46â†’ * Map text alignment to docx AlignmentType
    47â†’ */
    48â†’function getAlignment(textAlign?: 'left' | 'center' | 'right'): AlignmentType {
    49â†’  switch (textAlign) {
    50â†’    case 'center':
    51â†’      return AlignmentType.CENTER;
    52â†’    case 'right':
    53â†’      return AlignmentType.RIGHT;
    54â†’    case 'left':
    55â†’    default:
    56â†’      return AlignmentType.LEFT;
    57â†’  }
    58â†’}
    59â†’
    60â†’/**
    61â†’ * Get the font size for a heading level from theme
    62â†’ */
    63â†’function getHeadingSize(level: number, theme: ThemeData): number {
    64â†’  const sizes: Record<number, number> = {
    65â†’    1: theme.h1Size,
    66â†’    2: theme.h2Size,
    67â†’    3: theme.h3Size,
    68â†’    4: theme.h4Size,
    69â†’    5: theme.h5Size,
    70â†’    6: theme.h6Size,
