   106â†’          new TextRun({
   107â†’            text: element.content,
   108â†’            font: theme.headingFont,
   109â†’            size: pointsToHalfPoints(fontSize),
   110â†’            color: theme.headingColor.replace('#', ''),
   111â†’            bold: true,
   112â†’          })
   113â†’        );
   114â†’        break;
   115â†’
   116â†’      case 'bold':
   117â†’        textRuns.push(
   118â†’          new TextRun({
   119â†’            text: element.content,
   120â†’            font: theme.headingFont,
   121â†’            size: pointsToHalfPoints(fontSize),
   122â†’            color: theme.headingColor.replace('#', ''),
   123â†’            bold: true,
   124â†’          })
   125â†’        );
   126â†’        break;
   127â†’
   128â†’      case 'italic':
   129â†’        textRuns.push(
   130â†’          new TextRun({
   131â†’            text: element.content,
   132â†’            font: theme.headingFont,
   133â†’            size: pointsToHalfPoints(fontSize),
   134â†’            color: theme.headingColor.replace('#', ''),
   135â†’            bold: true,
   136â†’            italics: true,
   137â†’          })
   138â†’        );
   139â†’        break;
   140â†’
   141â†’      case 'code':
   142â†’        textRuns.push(
   143â†’          new TextRun({
