   145â†’            font: 'Courier New',
   146â†’            size: pointsToHalfPoints(fontSize - 1),
   147â†’            color: theme.textColor.replace('#', ''),
   148â†’            bold: true,
   149â†’          })
   150â†’        );
   151â†’        break;
   152â†’
   153â†’      case 'link':
   154â†’        // Note: For proper hyperlinks, we would need to use ExternalHyperlink
   155â†’        // For now, render as styled text
   156â†’        textRuns.push(
   157â†’          new TextRun({
   158â†’            text: element.content,
   159â†’            font: theme.headingFont,
   160â†’            size: pointsToHalfPoints(fontSize),
   161â†’            color: theme.linkColor.replace('#', ''),
   162â†’            bold: true,
   163â†’            underline: {},
   164â†’          })
   165â†’        );
   166â†’        break;
   167â†’
   168â†’      case 'strike':
   169â†’        textRuns.push(
   170â†’          new TextRun({
   171â†’            text: element.content,
   172â†’            font: theme.headingFont,
   173â†’            size: pointsToHalfPoints(fontSize),
   174â†’            color: theme.headingColor.replace('#', ''),
   175â†’            bold: true,
   176â†’            strike: true,
   177â†’          })
   178â†’        );
   179â†’        break;
   180â†’
   181â†’      case 'lineBreak':
   182â†’        textRuns.push(new TextRun({ break: 1 }));
   183â†’        break;
   184â†’
   185â†’      default:
   186â†’        // Fallback for unknown types
   187â†’        if (element.content) {
   188â†’          textRuns.push(
   189â†’            new TextRun({
   190â†’              text: element.content,
   191â†’              font: theme.headingFont,
   192â†’              size: pointsToHalfPoints(fontSize),
   193â†’              color: theme.headingColor.replace('#', ''),
   194â†’              bold: true,
   195â†’            })
   196â†’          );
   197â†’        }
   198â†’    }
   199â†’  }
   200â†’
   201â†’  return textRuns;
   202â†’}
