   204â†’/**
   205â†’ * Convert a heading markdown element to a docx Paragraph
   206â†’ */
   207â†’export function createHeadingParagraph(
   208â†’  element: MarkdownElement,
   209â†’  theme: ThemeData
   210â†’): Paragraph {
   211â†’  const level = element.level || 1;
   212â†’  const fontSize = getHeadingSize(level, theme);
   213â†’
   214â†’  // Build children (TextRuns) from inline elements or plain content
   215â†’  let children: TextRun[];
   216â†’
   217â†’  if (element.inline && element.inline.length > 0) {
   218â†’    children = convertInlineElements(element.inline, theme, fontSize);
   219â†’  } else {
