   124â†’ */
   125â†’export function createImageParagraph(
   126â†’  element: MarkdownElement,
   127â†’  theme: ThemeData
   128â†’): Paragraph {
   129â†’  const imageSrc = element.src;
   130â†’
   131â†’  // If no source, return a placeholder paragraph
   132â†’  if (!imageSrc) {
   133â†’    return createFallbackParagraph('[Image: no source provided]', element.alt, theme);
   134â†’  }
   135â†’
   136â†’  try {
   137â†’    let imageData: Buffer;
   138â†’    let imageType: 'png' | 'jpg' | 'gif' | 'bmp';
   139â†’
