   141â†’    if (imageSrc.startsWith('data:')) {
   142â†’      // Base64 data URL
   143â†’      const parsed = parseDataUrl(imageSrc);
   144â†’      if (!parsed) {
   145â†’        return createFallbackParagraph('[Image: invalid data URL]', element.alt, theme);
   146â†’      }
   147â†’      imageData = parsed.buffer;
   148â†’      imageType = parsed.type;
   149â†’    } else if (imageSrc.startsWith('http://') || imageSrc.startsWith('https://')) {
   150â†’      // Remote URL - we can't load these synchronously in docx generation
   151â†’      // Return a placeholder with the URL
   152â†’      return createFallbackParagraph(`[Image: ${element.alt || 'external image'}]`, imageSrc, theme);
   153â†’    } else {
   154â†’      // Local file path
   155â†’      const filePath = imageSrc.startsWith('file://') ? imageSrc.slice(7) : imageSrc;
   156â†’
   157â†’      if (!fs.existsSync(filePath)) {
   158â†’        return createFallbackParagraph('[Image not found]', element.alt || filePath, theme);
   159â†’      }
   160â†’
   161â†’      imageData = fs.readFileSync(filePath);
   162â†’      imageType = getImageTypeFromPath(filePath);
   163â†’    }
   164â†’
   165â†’    // Calculate dimensions
   166â†’    const dimensions = calculateDimensions();
