   168â†’    // Create the ImageRun
   169â†’    const imageRun = new ImageRun({
   170â†’      type: imageType,
   171â†’      data: imageData,
   172â†’      transformation: {
   173â†’        width: dimensions.width,
   174â†’        height: dimensions.height,
   175â†’      },
   176â†’    });
   177â†’
   178â†’    // Build paragraph children
   179â†’    const children: (ImageRun | TextRun)[] = [imageRun];
   180â†’
   181â†’    // Add alt text as caption below the image if provided
   182â†’    if (element.alt) {
   183â†’      // Add line break and caption text
   184â†’      children.push(
   185â†’        new TextRun({ break: 1 }),
   186â†’        new TextRun({
   187â†’          text: element.alt,
   188â†’          font: theme.bodyFont,
   189â†’          size: pointsToHalfPoints(theme.bodySize - 2),
   190â†’          color: theme.textColor.replace('#', ''),
   191â†’          italics: true,
   192â†’        })
   193â†’      );
   194â†’    }
   195â†’
   196â†’    return new Paragraph({
   197â†’      alignment: AlignmentType.CENTER,
   198â†’      spacing: {
   199â†’        before: pointsToDxa(12 * 0.75),
   200â†’        after: pointsToDxa(12 * 0.75),
   201â†’      },
   202â†’      children,
   203â†’    });
   204â†’  } catch (error) {
   205â†’    // If anything fails, return a fallback paragraph
   206â†’    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
   207â†’    return createFallbackParagraph(`[Image failed to load: ${errorMessage}]`, element.alt, theme);
   208â†’  }
