   203â†’
   204â†’  return new Table({
   205â†’    width: {
   206â†’      size: 100,
   207â†’      type: WidthType.PERCENTAGE,
   208â†’    },
   209â†’    rows: tableRows,
   210â†’  });
   211â†’}
