     1â†’/**
     2â†’ * Heading Element Converter for DOCX
     3â†’ * Converts markdown heading elements to Word document paragraphs with heading styles
     4â†’ */
     5â†’
     6â†’import {
     7â†’  AlignmentType,
     8â†’  HeadingLevel,
     9â†’  Paragraph,
    10â†’  TextRun,
    11â†’} from 'docx';
    12â†’import type { ThemeData } from '@shared/types/ipc-contracts.js';
    13â†’import type { InlineElement, MarkdownElement } from '../../pdf/markdown-parser.js';
    14â†’
    15â†’/**
    16â†’ * Convert points to DXA units (20 DXA = 1 point)
    17â†’ */
    18â†’function pointsToDxa(points: number): number {
    19â†’  return Math.round(points * 20);
    20â†’}
    21â†’
    22â†’/**
    23â†’ * Convert points to half-points (used for font sizes in docx)
    24â†’ * Font sizes in docx are specified in half-points (1 point = 2 half-points)
    25â†’ */
    26â†’function pointsToHalfPoints(points: number): number {
    27â†’  return Math.round(points * 2);
    28â†’}
    29â†’
    30â†’/**
    31â†’ * Map heading levels to docx HeadingLevel enum
    32â†’ */
    33â†’function getHeadingLevel(level: number): HeadingLevel {
    34â†’  const headingLevels: Record<number, HeadingLevel> = {
    35â†’    1: HeadingLevel.HEADING_1,
    36â†’    2: HeadingLevel.HEADING_2,
    37â†’    3: HeadingLevel.HEADING_3,
    38â†’    4: HeadingLevel.HEADING_4,
    39â†’    5: HeadingLevel.HEADING_5,
    40â†’    6: HeadingLevel.HEADING_6,
    41â†’  };
    42â†’  return headingLevels[level] || HeadingLevel.HEADING_1;
    43â†’}
    44â†’
    45â†’/**
    46â†’ * Map text alignment to docx AlignmentType
    47â†’ */
    48â†’function getAlignment(textAlign?: 'left' | 'center' | 'right'): AlignmentType {
    49â†’  switch (textAlign) {
    50â†’    case 'center':
    51â†’      return AlignmentType.CENTER;
    52â†’    case 'right':
    53â†’      return AlignmentType.RIGHT;
    54â†’    case 'left':
    55â†’    default:
    56â†’      return AlignmentType.LEFT;
    57â†’  }
    58â†’}
    59â†’
    60â†’/**
    61â†’ * Get the font size for a heading level from theme
    62â†’ */
    63â†’function getHeadingSize(level: number, theme: ThemeData): number {
    64â†’  const sizes: Record<number, number> = {
    65â†’    1: theme.h1Size,
    66â†’    2: theme.h2Size,
    67â†’    3: theme.h3Size,
    68â†’    4: theme.h4Size,
    69â†’    5: theme.h5Size,
    70â†’    6: theme.h6Size,
    71â†’  };
    72â†’  return sizes[level] || theme.h1Size;
    73â†’}
    74â†’
    75â†’/**
    76â†’ * Get margin top in DXA for heading level (converts from pixels)
    77â†’ * Approximate conversion: 1px â‰ˆ 15 DXA (at 96 DPI)
    78â†’ */
    79â†’function getMarginTop(level: number): number {
    80â†’  const marginTop: Record<number, number> = {
    81â†’    1: 24,
    82â†’    2: 20,
    83â†’    3: 16,
    84â†’    4: 12,
    85â†’    5: 10,
    86â†’    6: 8,
    87â†’  };
    88â†’  // Convert px to DXA (approximately 15 DXA per px)
    89â†’  return pointsToDxa((marginTop[level] || 24) * 0.75);
    90â†’}
    91â†’
    92â†’/**
    93â†’ * Convert inline elements to TextRun array for docx
    94â†’ */
    95â†’function convertInlineElements(
    96â†’  inlineElements: InlineElement[],
    97â†’  theme: ThemeData,
    98â†’  fontSize: number
    99â†’): TextRun[] {
   100â†’  const textRuns: TextRun[] = [];
   101â†’
   102â†’  for (const element of inlineElements) {
   103â†’    switch (element.type) {
   104â†’      case 'text':
   105â†’        textRuns.push(
   106â†’          new TextRun({
   107â†’            text: element.content,
   108â†’            font: theme.headingFont,
   109â†’            size: pointsToHalfPoints(fontSize),
   110â†’            color: theme.headingColor.replace('#', ''),
   111â†’            bold: true,
   112â†’          })
   113â†’        );
   114â†’        break;
   115â†’
   116â†’      case 'bold':
   117â†’        textRuns.push(
   118â†’          new TextRun({
   119â†’            text: element.content,
   120â†’            font: theme.headingFont,
   121â†’            size: pointsToHalfPoints(fontSize),
   122â†’            color: theme.headingColor.replace('#', ''),
   123â†’            bold: true,
   124â†’          })
   125â†’        );
   126â†’        break;
   127â†’
   128â†’      case 'italic':
   129â†’        textRuns.push(
   130â†’          new TextRun({
   131â†’            text: element.content,
   132â†’            font: theme.headingFont,
   133â†’            size: pointsToHalfPoints(fontSize),
   134â†’            color: theme.headingColor.replace('#', ''),
   135â†’            bold: true,
   136â†’            italics: true,
   137â†’          })
   138â†’        );
   139â†’        break;
   140â†’
   141â†’      case 'code':
   142â†’        textRuns.push(
   143â†’          new TextRun({
   144â†’            text: element.content,
   145â†’            font: 'Courier New',
   146â†’            size: pointsToHalfPoints(fontSize - 1),
   147â†’            color: theme.textColor.replace('#', ''),
   148â†’            bold: true,
   149â†’          })
   150â†’        );
   151â†’        break;
   152â†’
   153â†’      case 'link':
   154â†’        // Note: For proper hyperlinks, we would need to use ExternalHyperlink
   155â†’        // For now, render as styled text
   156â†’        textRuns.push(
   157â†’          new TextRun({
   158â†’            text: element.content,
   159â†’            font: theme.headingFont,
   160â†’            size: pointsToHalfPoints(fontSize),
   161â†’            color: theme.linkColor.replace('#', ''),
   162â†’            bold: true,
   163â†’            underline: {},
   164â†’          })
   165â†’        );
   166â†’        break;
   167â†’
   168â†’      case 'strike':
   169â†’        textRuns.push(
   170â†’          new TextRun({
   171â†’            text: element.content,
   172â†’            font: theme.headingFont,
   173â†’            size: pointsToHalfPoints(fontSize),
   174â†’            color: theme.headingColor.replace('#', ''),
   175â†’            bold: true,
   176â†’            strike: true,
   177â†’          })
   178â†’        );
   179â†’        break;
   180â†’
   181â†’      case 'lineBreak':
   182â†’        textRuns.push(new TextRun({ break: 1 }));
   183â†’        break;
   184â†’
   185â†’      default:
   186â†’        // Fallback for unknown types
   187â†’        if (element.content) {
   188â†’          textRuns.push(
   189â†’            new TextRun({
   190â†’              text: element.content,
   191â†’              font: theme.headingFont,
   192â†’              size: pointsToHalfPoints(fontSize),
   193â†’              color: theme.headingColor.replace('#', ''),
   194â†’              bold: true,
   195â†’            })
   196â†’          );
   197â†’        }
   198â†’    }
   199â†’  }
   200â†’
   201â†’  return textRuns;
   202â†’}
   203â†’
   204â†’/**
   205â†’ * Convert a heading markdown element to a docx Paragraph
   206â†’ */
   207â†’export function createHeadingParagraph(
   208â†’  element: MarkdownElement,
   209â†’  theme: ThemeData
   210â†’): Paragraph {
   211â†’  const level = element.level || 1;
   212â†’  const fontSize = getHeadingSize(level, theme);
   213â†’
   214â†’  // Build children (TextRuns) from inline elements or plain content
   215â†’  let children: TextRun[];
   216â†’
   217â†’  if (element.inline && element.inline.length > 0) {
   218â†’    children = convertInlineElements(element.inline, theme, fontSize);
   219â†’  } else {
   220â†’    // Plain text content
   221â†’    children = [
   222â†’      new TextRun({
   223â†’        text: element.content,
   224â†’        font: theme.headingFont,
   225â†’        size: pointsToHalfPoints(fontSize),
   226â†’        color: theme.headingColor.replace('#', ''),
   227â†’        bold: true,
   228â†’      }),
   229â†’    ];
   230â†’  }
   231â†’
   232â†’  return new Paragraph({
   233â†’    heading: getHeadingLevel(level),
   234â†’    alignment: getAlignment(element.textAlign),
   235â†’    spacing: {
   236â†’      before: getMarginTop(level),
   237â†’      after: pointsToDxa(8 * 0.75), // 8px margin bottom
   238â†’    },
   239â†’    children,
   240â†’  });
   241â†’}
