     4â†’ */
     5â†’
     6â†’import {
     7â†’  AlignmentType,
     8â†’  Paragraph,
     9â†’  ShadingType,
    10â†’  TextRun,
    11â†’} from 'docx';
    12â†’import type { ThemeData } from '@shared/types/ipc-contracts.js';
    13â†’import type { MarkdownElement } from '../../pdf/markdown-parser.js';
    14â†’
    15â†’/**
    16â†’ * Convert points to DXA units (20 DXA = 1 point)
    17â†’ */
    18â†’function pointsToDxa(points: number): number {
    19â†’  return Math.round(points * 20);
    20â†’}
    21â†’
    22â†’/**
    23â†’ * Convert points to half-points (used for font sizes in docx)
    24â†’ * Font sizes in docx are specified in half-points (1 point = 2 half-points)
    25â†’ */
    26â†’function pointsToHalfPoints(points: number): number {
    27â†’  return Math.round(points * 2);
    28â†’}
    29â†’
    30â†’/**
    31â†’ * Preserve leading whitespace by converting spaces to non-breaking spaces
    32â†’ * This ensures code indentation is preserved in the Word document
    33â†’ */
    34â†’function preserveLeadingWhitespace(line: string): string {
    35â†’  return line.replace(/^ +/, (spaces) => '\u00A0'.repeat(spaces.length));
    36â†’}
    37â†’
    38â†’/**
    39â†’ * Convert a code block markdown element to an array of docx Paragraphs
    40â†’ * Each line of code becomes its own paragraph for proper line-by-line rendering
    41â†’ */
    42â†’export function createCodeBlockParagraphs(
    43â†’  element: MarkdownElement,
    44â†’  theme: ThemeData
    45â†’): Paragraph[] {
    46â†’  const paragraphs: Paragraph[] = [];
    47â†’  const fontSize = theme.bodySize - 1; // Slightly smaller font for code
    48â†’  const lines = element.content.split('\n');
    49â†’
    50â†’  // Get background color (strip # prefix for docx)
    51â†’  const backgroundColor = theme.codeBackground.replace('#', '');
    52â†’
    53â†’  for (let idx = 0; idx < lines.length; idx++) {
    54â†’    const line = lines[idx];
    55â†’    const isFirstLine = idx === 0;
    56â†’    const isLastLine = idx === lines.length - 1;
    57â†’
    58â†’    // Preserve leading whitespace for code indentation
    59â†’    const preservedLine = preserveLeadingWhitespace(line);
    60â†’
    61â†’    // Create TextRun for this line of code
    62â†’    // Empty lines still need a space or non-breaking space to maintain height
    63â†’    const textContent = preservedLine === '' ? '\u00A0' : preservedLine;
    64â†’
    65â†’    const textRun = new TextRun({
    66â†’      text: textContent,
    67â†’      font: 'Courier New',
    68â†’      size: pointsToHalfPoints(fontSize),
    69â†’      color: theme.textColor.replace('#', ''),
    70â†’    });
    71â†’
    72â†’    // Calculate spacing - add top margin for first line, bottom margin for last line
    73â†’    // Minimal spacing between lines for code blocks
    74â†’    const spacingBefore = isFirstLine ? pointsToDxa(12 * 0.75) : 0;
    75â†’    const spacingAfter = isLastLine ? pointsToDxa(12 * 0.75) : pointsToDxa(1);
    76â†’
    77â†’    paragraphs.push(
    78â†’      new Paragraph({
    79â†’        alignment: AlignmentType.LEFT,
    80â†’        shading: {
    81â†’          type: ShadingType.SOLID,
    82â†’          color: backgroundColor,
    83â†’          fill: backgroundColor,
    84â†’        },
    85â†’        // Add padding effect using indentation
    86â†’        indent: {
    87â†’          left: pointsToDxa(12), // 12pt padding left
    88â†’          right: pointsToDxa(12), // 12pt padding right
    89â†’        },
    90â†’        spacing: {
    91â†’          before: spacingBefore,
    92â†’          after: spacingAfter,
    93â†’          line: 360, // 1.5 line spacing (240 = single, 360 = 1.5)
    94â†’        },
    95â†’        children: [textRun],
    96â†’      })
    97â†’    );
    98â†’  }
    99â†’
   100â†’  return paragraphs;
   101â†’}
