   182â†’    } else {
   183â†’      currentPage.push(element);
   184â†’    }
   185â†’  }
   186â†’
   187â†’  // Add remaining content as last page
   188â†’  if (currentPage.length > 0) {
   189â†’    pages.push(currentPage);
   190â†’  }
   191â†’
   192â†’  // If no content, add empty page
