     5â†’
     6â†’import {
     7â†’  BorderStyle,
     8â†’  Paragraph,
     9â†’  ShadingType,
    10â†’  Table,
    11â†’  TableCell,
    12â†’  TableRow,
    13â†’  TextRun,
    14â†’  WidthType,
    15â†’} from 'docx';
    16â†’import type { ThemeData } from '@shared/types/ipc-contracts.js';
    17â†’import type { MarkdownElement } from '../../pdf/markdown-parser.js';
    18â†’
    19â†’/**
    20â†’ * Convert points to DXA units (20 DXA = 1 point)
    21â†’ */
    22â†’function pointsToDxa(points: number): number {
    23â†’  return Math.round(points * 20);
    24â†’}
    25â†’
    26â†’/**
    27â†’ * Convert points to half-points (used for font sizes in docx)
    28â†’ * Font sizes in docx are specified in half-points (1 point = 2 half-points)
    29â†’ */
    30â†’function pointsToHalfPoints(points: number): number {
    31â†’  return Math.round(points * 2);
    32â†’}
    33â†’
    34â†’/**
    35â†’ * Create standard table cell borders
    36â†’ */
    37â†’function createCellBorders(color: string) {
    38â†’  return {
    39â†’    top: { style: BorderStyle.SINGLE, size: 8, color },
    40â†’    bottom: { style: BorderStyle.SINGLE, size: 8, color },
    41â†’    left: { style: BorderStyle.SINGLE, size: 8, color },
    42â†’    right: { style: BorderStyle.SINGLE, size: 8, color },
    43â†’  };
    44â†’}
    45â†’
    46â†’/**
    47â†’ * Create a header cell with bold text and background shading
    48â†’ */
    49â†’function createHeaderCell(
    50â†’  content: string,
    51â†’  theme: ThemeData,
    52â†’  columnCount: number
    53â†’): TableCell {
    54â†’  const fontSize = theme.bodySize;
    55â†’  const borderColor = theme.textColor.replace('#', '');
    56â†’  const backgroundColor = theme.codeBackground.replace('#', '');
    57â†’
    58â†’  return new TableCell({
    59â†’    width: {
    60â†’      size: Math.floor(100 / columnCount),
    61â†’      type: WidthType.PERCENTAGE,
    62â†’    },
    63â†’    shading: {
    64â†’      type: ShadingType.SOLID,
    65â†’      color: backgroundColor,
    66â†’      fill: backgroundColor,
    67â†’    },
    68â†’    borders: createCellBorders(borderColor),
    69â†’    margins: {
    70â†’      top: pointsToDxa(6 * 0.75),
    71â†’      bottom: pointsToDxa(6 * 0.75),
    72â†’      left: pointsToDxa(6 * 0.75),
    73â†’      right: pointsToDxa(6 * 0.75),
    74â†’    },
    75â†’    children: [
    76â†’      new Paragraph({
    77â†’        children: [
    78â†’          new TextRun({
    79â†’            text: content,
    80â†’            font: theme.bodyFont,
    81â†’            size: pointsToHalfPoints(fontSize),
    82â†’            color: theme.textColor.replace('#', ''),
    83â†’            bold: true,
    84â†’          }),
    85â†’        ],
    86â†’      }),
    87â†’    ],
    88â†’  });
    89â†’}
    90â†’
    91â†’/**
    92â†’ * Create a data cell with regular text
    93â†’ */
    94â†’function createDataCell(
    95â†’  content: string,
    96â†’  theme: ThemeData,
    97â†’  columnCount: number
    98â†’): TableCell {
    99â†’  const fontSize = theme.bodySize;
   100â†’  const borderColor = theme.textColor.replace('#', '');
   101â†’
   102â†’  return new TableCell({
   103â†’    width: {
   104â†’      size: Math.floor(100 / columnCount),
   105â†’      type: WidthType.PERCENTAGE,
   106â†’    },
   107â†’    borders: createCellBorders(borderColor),
   108â†’    margins: {
   109â†’      top: pointsToDxa(6 * 0.75),
   110â†’      bottom: pointsToDxa(6 * 0.75),
   111â†’      left: pointsToDxa(6 * 0.75),
   112â†’      right: pointsToDxa(6 * 0.75),
   113â†’    },
   114â†’    children: [
   115â†’      new Paragraph({
   116â†’        children: [
   117â†’          new TextRun({
   118â†’            text: content,
   119â†’            font: theme.bodyFont,
   120â†’            size: pointsToHalfPoints(fontSize),
   121â†’            color: theme.textColor.replace('#', ''),
   122â†’          }),
   123â†’        ],
   124â†’      }),
   125â†’    ],
   126â†’  });
   127â†’}
   128â†’
   129â†’/**
   130â†’ * Create a header row from headers array
   131â†’ */
   132â†’function createHeaderRow(
   133â†’  headers: string[],
   134â†’  theme: ThemeData
   135â†’): TableRow {
   136â†’  const cells = headers.map((header) =>
   137â†’    createHeaderCell(header, theme, headers.length)
   138â†’  );
   139â†’
   140â†’  return new TableRow({
   141â†’    tableHeader: true,
   142â†’    children: cells,
   143â†’  });
   144â†’}
   145â†’
   146â†’/**
   147â†’ * Create a data row from cell values array
   148â†’ */
   149â†’function createDataRow(
   150â†’  cells: string[],
   151â†’  theme: ThemeData,
   152â†’  columnCount: number
   153â†’): TableRow {
   154â†’  // Ensure we have the right number of cells (pad with empty if needed)
   155â†’  const normalizedCells = [...cells];
   156â†’  while (normalizedCells.length < columnCount) {
   157â†’    normalizedCells.push('');
   158â†’  }
   159â†’
   160â†’  const tableCells = normalizedCells.map((cell) =>
   161â†’    createDataCell(cell, theme, columnCount)
   162â†’  );
   163â†’
   164â†’  return new TableRow({
   165â†’    children: tableCells,
   166â†’  });
   167â†’}
   168â†’
   169â†’/**
   170â†’ * Convert a table markdown element to a docx Table
   171â†’ */
   172â†’export function createTableElement(
   173â†’  element: MarkdownElement,
   174â†’  theme: ThemeData
   175â†’): Table {
   176â†’  const headers = element.headers || [];
   177â†’  const rows = element.rows || [];
   178â†’
   179â†’  // Determine column count from headers or first row
   180â†’  const columnCount = headers.length > 0
   181â†’    ? headers.length
   182â†’    : rows.length > 0 && rows[0]
   183â†’      ? rows[0].length
   184â†’      : 1;
   185â†’
   186â†’  // Build table rows
   187â†’  const tableRows: TableRow[] = [];
   188â†’
   189â†’  // Add header row if headers exist
   190â†’  if (headers.length > 0) {
   191â†’    tableRows.push(createHeaderRow(headers, theme));
   192â†’  }
   193â†’
   194â†’  // Add data rows
   195â†’  for (const row of rows) {
   196â†’    tableRows.push(createDataRow(row, theme, columnCount));
   197â†’  }
   198â†’
   199â†’  // Handle empty table case
   200â†’  if (tableRows.length === 0) {
   201â†’    tableRows.push(createDataRow([''], theme, 1));
   202â†’  }
   203â†’
   204â†’  return new Table({
   205â†’    width: {
   206â†’      size: 100,
   207â†’      type: WidthType.PERCENTAGE,
   208â†’    },
   209â†’    rows: tableRows,
   210â†’  });
   211â†’}
