   190â†’/**
   191â†’ * Convert a table markdown element to a docx Table
   192â†’ */
   193â†’export function createTableElement(
   194â†’  element: MarkdownElement,
   195â†’  theme: ThemeData
   196â†’): Table {
   197â†’  const headers = element.headers || [];
   198â†’  const rows = element.rows || [];
   199â†’
   200â†’  // Determine column count from headers or first row
   201â†’  const columnCount = headers.length > 0
   202â†’    ? headers.length
