   216â†’  // Build table rows
   217â†’  const tableRows: TableRow[] = [];
   218â†’
   219â†’  // Add header row if headers exist
   220â†’  if (headers.length > 0) {
   221â†’    tableRows.push(createHeaderRow(headers, theme, cellWidthDxa));
   222â†’  }
   223â†’
   224â†’  // Add data rows
   225â†’  for (const row of rows) {
   226â†’    tableRows.push(createDataRow(row, theme, columnCount, cellWidthDxa));
   227â†’  }
