   234â†’  return new Table({
   235â†’    width: {
   236â†’      size: tableWidthDxa,
   237â†’      type: WidthType.DXA,
   238â†’    },
   239â†’    columnWidths: columnWidths,
   240â†’    rows: tableRows,
